mapper.insert:
INSERT INTO BOARD(
    SN          /* 등록번호 */
    , TITLE       /* 등록제목 */
    , CN          /* 등록내용 */
    , MODIFY_DATE /* 등록일시 */
    , FILE_SN     /*  */
) VALUES (
    BOARD_SEQ.NEXTVAL
    , #{title}
    , #{cn}
    , SYSDATE    
    , #{fileSn}
)

mapper.update:
UPDATE BOARD SET 
    TITLE = #{title}
    , CN = #{cn}
WHERE
    SN = #{sn}
